mapper.delete:
delete from thumbs_up_record
where user_id = #{userId,jdbcType=VARCHAR}
and article_id = #{articleId,jdbcType=VARCHAR}
and thumbs_up_type = #{thumbsUpType,jdbcType=VARCHAR}

mapper.update:
update thumbs_up_record
set thumbs_up_date = #{thumbsUpDate,jdbcType=TIMESTAMP}
where user_id = #{userId,jdbcType=BIGINT}
and article_id = #{articleId,jdbcType=BIGINT}
and thumbs_up_type = #{thumbsUpType,jdbcType=VARCHAR}

mapper.select:
select user_id, article_id, thumbs_up_type, thumbs_up_date
from thumbs_up_record
where user_id = #{userId,jdbcType=BIGINT}
and article_id = #{articleId,jdbcType=BIGINT}
and thumbs_up_type = #{thumbsUpType,jdbcType=VARCHAR}
